Toolbar.Script:
function myOpen(el) {
$('#mySub').css('visibility','hidden');
$('#mySub').load("Topics/All-others/menu" + el + ".htm");
$('#mySub').css('visibility','visible');
x=$('#myMenu' + el).offset().left;
y=$('#myMenu' + el).offset().top;
$('#mySub').show().css({'top':y+22,'left':x+4});
}


function myClose() { $('#mySub').css('visibility','hidden'); }


var timer1;
function myTimer() { timer1 = setTimeout(function() { myClose() }, 100); }


function myCancel() { clearTimeout(timer1); }


$(document).ready(function() {

$('head').append('<!-- Standard iPhone --><link rel="apple-touch-icon" sizes="144x144" href="Images/Icons/apple-touch-icon.png" />');

$("body").append("<div id='mySub' class='menus'></div>")

$('button.overview-button').attr('id','myMenu1');
$('button.maintenance-button').attr('id','myMenu2');
$('button.control_sys-button').attr('id','myMenu3');
$('button.drawings_pl-button').attr('id','myMenu4');
$('button.contact_us-button').attr('id','myMenu5');   

$('button.overview-button').html('');
$('button.maintenance-button').html('');
$('button.control_sys-button').html('');
$('button.drawings_pl-button').html('');
$('button.contact_us-button').html('');

$('button.overview-button').css('color','red');
$('button.maintenance-button').css('color','red');
$('button.control_sys-button').css('color','red');
$('button.drawings_pl-button').css('color','red');
$('button.contact_us-button').css('color','red');

$('button.overview-button').css('text-align','center');
$('button.maintenance-button').css('text-align','center');
$('button.control_sys-button').css('text-align','center');
$('button.drawings_pl-button').css('text-align','center');
$('button.contact_us-button').css('text-align','center');

$('#myMenu1').bind('mouseover',function(){myOpen('1')});
$('#myMenu2').bind('mouseover',function(){myOpen('2')});
$('#myMenu3').bind('mouseover',function(){myOpen('3')});
$('#myMenu4').bind('mouseover',function(){myOpen('4')});


$('#mySub').bind('mouseover',function(){myCancel()});

$('#mySub').bind('mouseout',function(){myTimer()});


$( "<style>.menus { width:200px; background-color: #f7f7f7; border:1px solid #c0c0c0; position:absolute; visibility:hidden; padding-left:4px; z-index:99; box-shadow:3px 6px 6px -3px #ccc; } </style>" ).appendTo( 

"head" )


}
);

