function contactUs() {
$('#searchPane').hide();
$('#topic').show();
window.open('Topics/ROV system hardware/rov_sys_overview_cust_service.htm', 'topic');
}

function aboutHelp() {
$('#searchPane').hide();
$('#topic').show();
window.open('Topics/ROV system hardware/rov_system_help_using_digital_manual_head-only.htm', 'topic');
}

function myOpen(el) {



$('#mySub').load("Content/menu" + el + ".htm");



$('#mySub').css('visibility','visible');



x=$('#myMenu' + el).offset().left;



y=$('#myMenu' + el).offset().top;



$('#mySub').show().css({'top':y+22,'left':x+4});
}







function myClose() {
 $('#mySub').css('visibility','hidden');
 }



var timer1;



function myTimer() { 
timer1 = setTimeout(function() { myClose(); }, 100)
 }







function myCancel() { clearTimeout(timer1) }

